import{t as o,v as d,x as r,y as a}from"./chunks/vue-vendor.WMOLkKO2.js";import"./app.De9tul0e.js";import"./chunks/vendor.CimhImBR.js";const w=JSON.parse('{"title":"User","description":"","frontmatter":{},"headers":[],"relativePath":"linux/user.md","filePath":"linux/user.md"}'),s={name:"linux/user.md"};function u(c,e,l,n,i,t){return r(),d("div",null,e[0]||(e[0]=[a('<h1 id="user" tabindex="-1">User <a class="header-anchor" href="#user" aria-label="Permalink to &quot;User&quot;">​</a></h1><h2 id="_1-添加用户" tabindex="-1">1. <strong>添加用户</strong> <a class="header-anchor" href="#_1-添加用户" aria-label="Permalink to &quot;1. **添加用户**&quot;">​</a></h2><ul><li><code>useradd</code>：用于创建一个新用户。例如，<code>sudo useradd newuser</code> 会创建一个名为 newuser 的新用户。</li><li><code>adduser</code>：这是 <code>useradd</code> 命令的一个更友好的前端，提供了交互式界面来设置用户密码和其他信息。例如，<code>sudo adduser newuser</code>。</li></ul><h2 id="_2-删除用户" tabindex="-1">2. <strong>删除用户</strong> <a class="header-anchor" href="#_2-删除用户" aria-label="Permalink to &quot;2. **删除用户**&quot;">​</a></h2><ul><li><code>userdel</code>：用于删除一个用户。例如，<code>sudo userdel newuser</code> 会删除名为 newuser 的用户。如果希望同时删除用户的家目录和邮件池，可以使用 <code>-r</code> 选项：<code>sudo userdel -r newuser</code>。</li></ul><h2 id="_3-修改用户信息" tabindex="-1">3. <strong>修改用户信息</strong> <a class="header-anchor" href="#_3-修改用户信息" aria-label="Permalink to &quot;3. **修改用户信息**&quot;">​</a></h2><ul><li><code>usermod</code>：用于修改用户信息，如用户名、用户组、家目录等。例如，<code>sudo usermod -l newusername oldusername</code> 会将 oldusername 改名为 newusername。</li><li><code>passwd</code>：用于更改用户密码。例如，<code>sudo passwd newuser</code> 会提示你为 newuser 用户设置新密码。</li></ul><h2 id="_4-查看用户信息" tabindex="-1">4. <strong>查看用户信息</strong> <a class="header-anchor" href="#_4-查看用户信息" aria-label="Permalink to &quot;4. **查看用户信息**&quot;">​</a></h2><ul><li><code>id</code>：显示用户的 UID（用户ID）、GID（组ID）以及用户所属的所有组。例如，<code>id newuser</code> 会显示 newuser 用户的相关信息。</li><li><code>who</code>：显示当前登录系统的所有用户。</li><li><code>w</code>：显示当前登录用户的详细信息，包括他们正在执行的命令、登录时间、CPU 和内存使用情况等。</li><li><code>finger</code>：如果已安装，可以提供关于用户的更详细信息，如全名、办公地点、电话号码等。</li></ul><h2 id="_5-用户组管理" tabindex="-1">5. <strong>用户组管理</strong> <a class="header-anchor" href="#_5-用户组管理" aria-label="Permalink to &quot;5. **用户组管理**&quot;">​</a></h2><ul><li><code>groupadd</code>：添加一个新组。例如，<code>sudo groupadd newgroup</code> 会创建一个名为 newgroup 的新组。</li><li><code>groupdel</code>：删除一个组。例如，<code>sudo groupdel newgroup</code> 会删除名为 newgroup 的组。</li><li><code>usermod -aG</code>：将用户添加到一个或多个附加组。例如，<code>sudo usermod -aG newgroup newuser</code> 会将 newuser 用户添加到 newgroup 组中。</li><li><code>gpasswd</code>：用于管理组密码和组成员。例如，<code>sudo gpasswd newgroup</code> 可以设置 newgroup 组的密码或添加/删除组成员。</li></ul><h2 id="_6-切换用户" tabindex="-1">6. <strong>切换用户</strong> <a class="header-anchor" href="#_6-切换用户" aria-label="Permalink to &quot;6. **切换用户**&quot;">​</a></h2><ul><li><code>su</code>：用于切换当前用户。例如，<code>su newuser</code> 会切换到 newuser 用户（需要输入该用户的密码）。使用 <code>su -</code> 可以切换到 root 用户并加载 root 用户的环境变量。</li><li><code>sudo</code>：允许普通用户以超级用户（通常是 root）的权限执行命令。例如，<code>sudo somecommand</code> 会以 root 权限执行 somecommand 命令。</li></ul>',13)]))}const p=o(s,[["render",u]]);export{w as __pageData,p as default};
