import{_ as o,c as d,a2 as s,o as r}from"./chunks/framework.DN5Ma_nw.js";const w=JSON.parse('{"title":"Linux-用户","description":"","frontmatter":{"title":"Linux-用户"},"headers":[],"relativePath":"linux/user.md","filePath":"linux/user.md"}'),u={name:"linux/user.md"};function c(l,e,n,i,t,a){return r(),d("div",null,e[0]||(e[0]=[s("<ol><li><p><strong>添加用户</strong></p><ul><li><code>useradd</code>：用于创建一个新用户。例如，<code>sudo useradd newuser</code> 会创建一个名为 newuser 的新用户。</li><li><code>adduser</code>：这是 <code>useradd</code> 命令的一个更友好的前端，提供了交互式界面来设置用户密码和其他信息。例如，<code>sudo adduser newuser</code>。</li></ul></li><li><p><strong>删除用户</strong></p><ul><li><code>userdel</code>：用于删除一个用户。例如，<code>sudo userdel newuser</code> 会删除名为 newuser 的用户。如果希望同时删除用户的家目录和邮件池，可以使用 <code>-r</code> 选项：<code>sudo userdel -r newuser</code>。</li></ul></li><li><p><strong>修改用户信息</strong></p><ul><li><code>usermod</code>：用于修改用户信息，如用户名、用户组、家目录等。例如，<code>sudo usermod -l newusername oldusername</code> 会将 oldusername 改名为 newusername。</li><li><code>passwd</code>：用于更改用户密码。例如，<code>sudo passwd newuser</code> 会提示你为 newuser 用户设置新密码。 s</li></ul></li><li><p><strong>查看用户信息</strong></p><ul><li><code>id</code>：显示用户的 UID（用户ID）、GID（组ID）以及用户所属的所有组。例如，<code>id newuser</code> 会显示 newuser 用户的相关信息。</li><li><code>who</code>：显示当前登录系统的所有用户。</li><li><code>w</code>：显示当前登录用户的详细信息，包括他们正在执行的命令、登录时间、CPU 和内存使用情况等。</li><li><code>finger</code>：如果已安装，可以提供关于用户的更详细信息，如全名、办公地点、电话号码等。</li></ul></li><li><p><strong>用户组管理</strong></p><ul><li><code>groupadd</code>：添加一个新组。例如，<code>sudo groupadd newgroup</code> 会创建一个名为 newgroup 的新组。</li><li><code>groupdel</code>：删除一个组。例如，<code>sudo groupdel newgroup</code> 会删除名为 newgroup 的组。</li><li><code>usermod -aG</code>：将用户添加到一个或多个附加组。例如，<code>sudo usermod -aG newgroup newuser</code> 会将 newuser 用户添加到 newgroup 组中。</li><li><code>gpasswd</code>：用于管理组密码和组成员。例如，<code>sudo gpasswd newgroup</code> 可以设置 newgroup 组的密码或添加/删除组成员。</li></ul></li><li><p><strong>切换用户</strong></p><ul><li><code>su</code>：用于切换当前用户。例如，<code>su newuser</code> 会切换到 newuser 用户（需要输入该用户的密码）。使用 <code>su -</code> 可以切换到 root 用户并加载 root 用户的环境变量。</li><li><code>sudo</code>：允许普通用户以超级用户（通常是 root）的权限执行命令。例如，<code>sudo somecommand</code> 会以 root 权限执行 somecommand 命令。</li></ul></li></ol>",1)]))}const g=o(u,[["render",c]]);export{w as __pageData,g as default};
